import React from 'react'

const Header = (props) => {
  return (
    <h1>{props.course.name}</h1>
  )
}

const Content = (props) => {
  return (
    <div>
      <Part partName={props.parts[0].name} partExercises={props.parts[0].exercises} />
      <Part partName={props.parts[1].name} partExercises={props.parts[1].exercises} />
      <Part partName={props.parts[2].name} partExercises={props.parts[2].exercises} />
    </div>
  )
}

const Part = (props) => {
  return (
    <p>{props.partName} {props.partExercises}</p>
  )
}

const Total = (props) => {
  return (
    // <p>Number of exercises {props.parts[0].exercises + props.parts[1].exercises + props.parts[2].exercises}</p>
    <p>Number of exercises {props.parts.reduce(
      (prev, curr) => prev + curr,
      0
    )}</p>
  )
}

const App = () => {
  const course = {
    name: 'Half Stack application development',
    id: 1,
    parts: [
      {
        name: 'Fundamentals of React',
        exercises: 10,
        id: 1
      },
      {
        name: 'Using props to pass data',
        exercises: 7,
        id: 2
      },
      {
        name: 'State of a component',
        exercises: 14,
        id: 3
      }
    ]
  }

  return (
    <div>
      <Course course={course} />
    </div>
  )
}

const Course = ({ course }) => {
  return (
    <>
      <Header course={course} />
      <Content parts={course.parts} />
      <Total parts={course.parts} />
    </>
  )
}

export default App
